package day4;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int v=10;
		int n=20;
		n+=3; 
		//var=var+3
		System.out.println(n);
		//post increment-execution from left to right
		System.out.println(v++);
		System.out.println(v);
		//pre increment
		System.out.println(++v);
		
		for(int i=0;i<10;i++) {
			System.out.println(i);
		}

	}

}
