package day4;

public class Variable_example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int myVar, a = 23, b = 34;
		myVar = 10;

		System.out.println("My variable value is " + myVar);
		long lvar = 224698707777777l;
		System.out.println("Long variable value is " + lvar);
		float fvar = 0.22334f;
		System.out.println("Float variable is " + fvar);
		boolean bvar = true;
		System.out.println("bvar is " + bvar);
		String svar = "Preeti Negi";
		System.out.println("String is " + svar);

		System.out.println("Concatenation is " + a + b);
		System.out.println("Sum is " + (a + b));
		System.out.println("Remainder is " + (b % a));

	}

}
